import React, { useState, useEffect } from "react";
import PingDummy from "./PingDummy";
import "./style-ping.css";
import * as Mui from "@material-ui/core";
import ClearIcon from "@mui/icons-material/Clear";
import DeleteIcon from "@mui/icons-material/Delete";
import Autocomplete from "@mui/material/Autocomplete";
import { WindowTwoTone } from "@mui/icons-material";

const urlSaved = [
	{
		label: "www.zadara.com",
	},
	{
		label: "www.ynet.com",
	},
	{
		label: "www.google.com",
	},
];
function PingComponent() {
	const [isLoading, setIsLoading] = useState();
	const loadingTimeMS = 3000;

	// useEffect(() => {
	// 	if (isLoading) {
	// 		setTimeout(() => {
	// 			setIsLoading(false);
	// 		}, loadingTimeMS);
	// 	}
	// }, [isLoading, setIsLoading]);

	const [userUrl, setUserUrl] = useState();
	const [pingData, setPingData] = useState();
	const [urlData, setUrlData] = useState([]);
	const [packetCount, setPacketCount] = useState(1);

	function triggerPing() {
		fetch("/api", {
			method: "POST",
			headers: {
				"Content-type": "aplication/json",
				url: userUrl,
				packets: packetCount,
			},
		})
			.then((res) => res.json())
			.then((data) => {
				setPingData(data.pingData);
				setUrlData(data.urlData);
				setIsLoading(true);
			});
	}

	return (
		<>
			{isLoading && <Mui.CircularProgress />}
			<Mui.Box class="pingMain">
				<Mui.Typography variant="h3">Ping form</Mui.Typography>
				Host:{" "}
				<Mui.TextField
					value={userUrl}
					onChange={(event) => setUserUrl(event.target.value)}
					id="outlined-basic"
					label="Url's to ping"
					variant="outlined"
				/>
				<Mui.Button variant="outlined" onClick={triggerPing}>
					Run
				</Mui.Button>
				<Mui.Button onClick={() => setUserUrl(() => "")} variant="outlined">
					Delete url
				</Mui.Button>
				<Mui.Box>
					{" "}
					<Mui.Typography variant="h1" class="loader">
						{" "}
						Count: {packetCount}
					</Mui.Typography>{" "}
				</Mui.Box>
				<Mui.Slider
					className="slider"
					max={20}
					value={packetCount}
					onChange={(event, newValue) => {
						setPacketCount(newValue);
					}}
					aria-label="Disabled slider"
				/>
				<Mui.Typography variant="h3">Ouput:</Mui.Typography>
				<Mui.TextareaAutosize
					cols="80"
					rows="20"
					value={JSON.stringify(pingData, null, 2)}
				></Mui.TextareaAutosize>
			</Mui.Box>

			<PingDummy urlData={urlData} />
		</>
	);
}

export default PingComponent;
