import React, { useState, useEffect } from 'react';
import "./App.css";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Slider from '@mui/material/Slider';
import AppDummy from './AppDummy'

function App(redisObj) {
  const [data, setData] = React.useState(null);
  const [userUrl, setUserUrl] = useState('')
  const [postId, setPostId] = useState('')
  const [counterEachPing, setCounter] = useState(0)
  const [updatedValue, setUpdatedValue] = useState(0)

useEffect(() => {
  const url = "www.whatever.com"
  fetch("/api", {
      method: "POST",
      headers: {
        'Content-type' : "aplication/json"
              },
      body: JSON.stringify({url: url }) 
  })
   .then((res) => res.json(console.log(res, ' i am the res')))
   .then((data) => {
    setUpdatedValue(data.myValueUpdated) 



},[]);

function updateCounterEachPing() {
    setCounter(counterEachPing+1)
}

let counter = 5
  return <> <Box class="pingMain">    <h1>Ping form</h1>
      <AppDummy userUrl={userUrl} counter={counter} header1={'Top Ping Sites'} header2={'Qty'} 
      updatedValue={updatedValue}
      />
    Host: <input  onChange={event => setUserUrl(event.target.value)}></input>

  <Box style={{display:'flex'}}>    <h2 class="loader">  Count:  {counterEachPing}</h2> </Box>
    <Slider disabled defaultValue={30} aria-label="Disabled slider" style={{width:'50%', margin:'10px'}}/>
      <Button variant="outlined" onClick={updateCounterEachPing}>Run</Button>
    <h2>
    Ouput:
    </h2>








 <textarea  cols="50"
                rows="20" value={JSON.stringify(data, null, 2) }></textarea>


    </Box>

  <h1>{updatedValue}</h1>
  </>

})}

export default App;
